    var isFKey = ASPx.Key.F1 <= keyCode && keyCode <= 127; // 127 - F16 for MAC
    return ASPx.Key.Delete <= keyCode && !isSystemKey && !isFKey || keyCode == ASPx.Key.Backspace || keyCode == ASPx.Key.Space;
