var MultipleJsAnimationTransition = ASPx.CreateClass(JsAnimationTransition, {
    constructor: function(element, options) {
        this.constructor.prototype.constructor.call(this, element, options);
        this.properties = {};
    },
    Start: function(properties) {
        this.initProperties(properties);
        this.initTimer();
    },
    /* Private */
    initProperties: function(properties) {
        this.properties = properties;
        for(var propName in this.properties)
            if(properties[propName].from == undefined)
                properties[propName].from = this.getValueInternal(this.element, propName);
    },
    update: function(progress) {
        for(var propName in this.properties) {
            if(this.properties.hasOwnProperty(propName)) {
                var property = this.properties[propName];
                if(property.from != property.to)
                    this.setValueInternal(this.element, propName, property.unit, this.gatCalculatedValue(property.from, property.to, progress));
            }
        }
    }
});

var CssAnimationTransition = ASPx.CreateClass(AnimationTransitionBase, {
    constructor: function(element, options) {
        this.constructor.prototype.constructor.call(this, element, options);
        this.transitionPropertyName = AnimationUtils.CurrentTransition.property;
        this.eventName = AnimationUtils.CurrentTransition.event;
    },
    /* Public */
    Start: function(from, to) {
        AnimationTransitionBase.prototype.Start.call(this, from, to);
        // B251923
        // This code should be executed on the end of all calculation.
        // It is not correct to modify an object while animation is running.
        this.startTimerId = window.setTimeout(function() {
            if(this.from == this.to)
                this.onTransitionEnd();
            else {
                var isHidden = ASPx.GetElementOffsetHeight(this.element) == 0 && ASPx.GetElementOffsetWidth(this.element) == 0; // B252495
                if(!isHidden)
                    this.prepareElementBeforeAnimation();
                this.SetValue(this.to);
                if(isHidden)
                    this.onTransitionEnd();
            }
        }.aspxBind(this), 0);
    },
    Cancel: function() {
        window.clearTimeout(this.startTimerId);
        AnimationTransitionBase.prototype.Cancel.call(this);
        ASPx.Evt.DetachEventFromElement(this.element, this.eventName, CssAnimationTransition.transitionEnd);
        this.setValueInternal(this.element, this.transitionPropertyName, "", "");
        this.stopAnimation();
    },
    /* Private */
    prepareElementBeforeAnimation: function() {
        ASPx.Evt.AttachEventToElement(this.element, this.eventName, CssAnimationTransition.transitionEnd);
        var dummy = this.element.offsetHeight;
        this.element.style[this.transitionPropertyName] = this.getTransitionCssString();

        if(ASPx.Browser.Safari && ASPx.Browser.MacOSMobilePlatform && ASPx.Browser.MajorVersion >= 8) //T267914
            setTimeout(function() {
                if(this.element && this.element.aspxTransition) {
                    this.element.style[this.transitionPropertyName] = "";
                    this.element.aspxTransition.onTransitionEnd();
                }
            }.aspxBind(this), this.duration + 100);
    },
    stopAnimation: function() {
        this.SetValue(ASPx.GetCurrentStyle(this.element)[this.property]);
    },
    onTransitionEnd: function() {
        this.Cancel();
        this.performOnComplete();
    },
    getTransition: function() {
        return this.transition.Css;
    },
    getTransitionCssString: function() {
        return this.getTransitionCssStringInternal(this.getCssName(this.property));
    },
    getTransitionCssStringInternal: function(cssProperty) {
        return cssProperty + " " + this.duration + "ms " + this.getTransition();
    },
    getCssName: function(property) {
        switch(property) {
            case "marginLeft":
                return "margin-left";
            case "marginTop":
                return "margin-top";
        }
        return property;
    }
});

var MultipleCssAnimationTransition = ASPx.CreateClass(CssAnimationTransition, {
    constructor: function(element, options) {
        this.constructor.prototype.constructor.call(this, element, options);
        this.properties = null;
    },
    Start: function(properties) {
        this.properties = properties;
        this.forEachProperties(function(property, propName) {
            if(property.from !== undefined)
                this.setValueInternal(this.element, propName, property.unit, property.from);
        }.aspxBind(this));

        this.prepareElementBeforeAnimation();

        window.setTimeout(function() {
            this.forEachProperties(function(property, propName) {
                this.setValueInternal(this.element, propName, property.unit, property.to);
            }.aspxBind(this));
        }.aspxBind(this), 0);
    },
    /* Private */
    stopAnimation: function() {
        var style = ASPx.GetCurrentStyle(this.element);
        this.forEachProperties(function(property, propName) {
            this.setValueInternal(this.element, propName, "", style[propName]);
        }.aspxBind(this));
    },
    getTransitionCssString: function() {
        var str = "";
        this.forEachProperties(function(property, propName) {
            str += this.getTransitionCssStringInternal(this.getCssName(propName)) + ",";
        }.aspxBind(this));
        str = str.substring(0, str.length - 1);
        return str;
    },
    forEachProperties: function(func) {
        for(var propName in this.properties) {
            if(this.properties.hasOwnProperty(propName)) {
                var property = this.properties[propName];
                if(property.from == undefined)
                    property.from = this.getValueInternal(this.element, propName);
                if(property.from != property.to)
                    func(property, propName);
            }
        }
    }
});

