// Keyboard helper
var KbdHelper = ASPx.CreateClass(null, {

    constructor: function(control) {
        this.control = control;
    },
    
    Init: function() {
        KbdHelper.GlobalInit();
        var elements = this.getFocusableElements();
        for(var i = 0; i < elements.length; i++) {
            var element = elements[i];
            element.tabIndex = Math.max(element.tabIndex, 0);
        
            ASPx.Evt.AttachEventToElement(element, "click", function(e) { this.HandleClick(e); }.aspxBind(this));
            ASPx.Evt.AttachEventToElement(element, "focus", function(e) {             
                return this.onElementFocus(e);
            }.aspxBind(this));
            ASPx.Evt.AttachEventToElement(element, "blur", function () { this.onBlur(); }.aspxBind(this)); 
        }         
    },
    onElementFocus: function(e) {
        if(!this.CanFocus(e))
            return true;
        KbdHelper.active = this;
    },

    getFocusableElements: function() {
        return [this.GetFocusableElement()];    
    },
    GetFocusableElement: function() { return this.control.GetMainElement(); },
    canHandleNoFocusAction: function() { 
        var focusableElements = this.getFocusableElements();
        for(var i = 0; i < focusableElements.length; i++) {
            if(focusableElements[i] === _aspxGetFocusedElement())
                return false;
        }
        return true;
    },
    
    CanFocus: function(e) {
        var tag = ASPx.Evt.GetEventSource(e).tagName;
        return !(tag === "A" || tag === "TEXTAREA" || tag === "INPUT" || tag === "SELECT" || tag === "IFRAME" || tag === "OBJECT");
    },
    
    HandleClick: function(e) {
        if(!this.CanFocus(e))
            return;
        this.Focus();
    },
    Focus: function() {
        try {
            this.GetFocusableElement().focus();   
        } catch(e) {
        }
    },
    onBlur: function(){
        delete KbdHelper.active;
    },
    
    HandleKeyDown: function(e) { },    
    HandleKeyPress: function(e) { },    
    HandleKeyUp: function (e) { },
    HandleNoFocusAction: function(e) { },
    FocusByAccessKey: function () { }
});

KbdHelper.GlobalInit = function() {
    if(KbdHelper.ready)
        return;
    ASPx.Evt.AttachEventToDocument("keydown", KbdHelper.OnKeyDown);
    ASPx.Evt.AttachEventToDocument("keypress", KbdHelper.OnKeyPress);
    ASPx.Evt.AttachEventToDocument("keyup", KbdHelper.OnKeyUp);
    KbdHelper.ready = true;    
};

KbdHelper.swallowKey = false;
KbdHelper.accessKeys = { };

KbdHelper.ProcessKey = function(e, actionName) {
    if(!KbdHelper.active) 
        return;
    if (KbdHelper.active.canHandleNoFocusAction()) {
        KbdHelper.active["HandleNoFocusAction"](e, actionName);
        return;
    }
    //B208466
    var ctl = KbdHelper.active.control;
    if(ctl !== ASPx.GetControlCollection().Get(ctl.name)) {
        delete KbdHelper.active;
        return;
    }

    if(!KbdHelper.swallowKey) 
        KbdHelper.swallowKey = KbdHelper.active[actionName](e);
    if(KbdHelper.swallowKey)
        ASPx.Evt.PreventEvent(e);
};

KbdHelper.OnKeyDown = function(e) {
    KbdHelper.swallowKey = false;
    if(KbdHelper.TryAccessKey(KbdHelper.getKeyName(e)))
        ASPx.Evt.PreventEvent(e);
    else    
        KbdHelper.ProcessKey(e, "HandleKeyDown"); 
};
KbdHelper.OnKeyPress = function(e) { KbdHelper.ProcessKey(e, "HandleKeyPress"); };
KbdHelper.OnKeyUp = function(e) { KbdHelper.ProcessKey(e, "HandleKeyUp"); };

KbdHelper.RegisterAccessKey = function(obj) {
    var key;
    if(obj.accessKey)
        key = "CtrlShift" + obj.accessKey;
    else if(obj.keyTipModeShortcut)
        key = obj.keyTipModeShortcut;
    if(!key) return;
    KbdHelper.accessKeys[key.toLowerCase()] = obj.name;
};
KbdHelper.TryAccessKey = function(code) {
    var key = code.toLowerCase ? code.toLowerCase() : String.fromCharCode(code).toLowerCase();
    var name = KbdHelper.accessKeys[key];
    if(!name) return false;
    var obj = ASPx.GetControlCollection().Get(name);
    return KbdHelper.ClickAccessKey(obj);
};

KbdHelper.ClickAccessKey = function (control) {
    if (!control) return false;
    var el = control.GetMainElement();
    if (!el) return false;
    el.focus();
    setTimeout(function () {
        if (KbdHelper.active && KbdHelper.active.FocusByAccessKey)
            KbdHelper.active.FocusByAccessKey();
    }.aspxBind(this), 100);
    return true;
};

KbdHelper.getKeyName = function(e) {
    var name = "";
    if(e.altKey)
        name += "Alt";
    if(e.ctrlKey)
        name += "Ctrl";
    if(e.shiftKey)
        name += "Shift";
    var keyCode = e.key || e.code || String.fromCharCode(ASPx.Evt.GetKeyCode(e));
    if(keyCode.match(/key/i))
        name += keyCode.replace(/key/i, "");
    else if(keyCode.match(/digit/i))
        name += keyCode.replace(/digit/i, "");
    else if(keyCode.match(/arrow/i))
        name += keyCode.replace(/arrow/i, "");
    else if(keyCode.match(/ins/i))
        name += "Ins";
    else if(keyCode.match(/del/i))
        name += "Del";
    else if(keyCode.match(/back/i))
        name += "Back";
    else if(!keyCode.match(/alt/i) && !keyCode.match(/control/i) && !keyCode.match(/shift/i))
        name += keyCode;
    return name.replace(/^a-zA-Z0-9/, "");
};

AccessKeysHelper = ASPx.CreateClass(KbdHelper, {

    constructor: function (control) {
        this.constructor.prototype.constructor.call(this, control);
        this.accessKeysVisible = false;
        this.activeKey = null;
        this.accessKey = control.createAccessKey ? control.createAccessKey(control.accessKey) : new AccessKey(control.accessKey);
        this.accessKeys = this.accessKey.accessKeys;
        this.charIndex = 0;
        this.onFocusByAccessKey = null;
        this.onClose = null;
        this.manualStopProcessing = false;
        this.isActive = false;
        this.areAccessKeysShown = false;
    },

    Init: function (control) {
        KbdHelper.prototype.Init.call(this);
        KbdHelper.RegisterAccessKey(control);            
    },

    Add: function (accessKey) {
        this.accessKey.Add(accessKey);
    },

    HandleKeyDown: function (e) {
        var keyCode = ASPx.Evt.GetKeyCode(e);
        var stopProcessing = this.processKeyDown(keyCode);
        if (stopProcessing.value) {
            this.stopProcessing();
            if(this.onClosedOnEscape && (keyCode == ASPx.Key.Esc || stopProcessing.fireEvent))
                this.onClosedOnEscape();
        }
        return stopProcessing;
    },
    HandleNoFocusAction: function (e, actionName) {
        var keyCode = ASPx.Evt.GetKeyCode(e);
        if (this.onClosedOnEscape && keyCode == ASPx.Key.Esc && actionName == "HandleKeyDown")
            this.onClosedOnEscape();
    },
    Activate: function () {
        KbdHelper.ClickAccessKey(this.control);
        this.areAccessKeysShown = true;
    },
    Stop: function() {
        this.stopProcessing();
    },
    stopProcessing: function () {
        this.HideAccessKeys();
        if (KbdHelper.active && this.isActive) {
            this.isActive = false;
            KbdHelper.active.control.GetMainElement().blur();
            delete KbdHelper.active;
        }
    },
    onBlur: function() {
        if (this.manualStopProcessing) {
            this.manualStopProcessing = false;
            return;
        }
        this.HideAccessKeys();
        KbdHelper.prototype.onBlur.call(this);
    },
    processKeyDown: function (keyCode) {
        switch (keyCode) {
            case ASPx.Key.Left:
                this.TryMoveFocusLeft();
                return { value: false };
            case ASPx.Key.Right:
                this.TryMoveFocusRight();
                return { value: false };
            case ASPx.Key.Esc:
                if(this.control.hideAllPopups)
                    this.control.hideAllPopups(true, true);
                if(this.activeKey)
                    this.activeKey = this.activeKey.Return();
                this.charIndex = 0;
                if (!this.activeKey)
                    return { value: true };
                break;
            case ASPx.Key.Enter:
                return { value: true };
            default:
                if (!ASPx.IsPrintableKey(keyCode))
                    return { value: false };
                var char = String.fromCharCode(keyCode).toUpperCase();
                var needToContinue = { value: false };
                var keyResult;
                if(this.activeKey)
                    keyResult = this.activeKey.TryAccessKey(char, this.charIndex, needToContinue);
                if (needToContinue.value) {
                    this.charIndex++;
                    return { value: false };
                }
                this.charIndex = 0;
                if(keyResult !== undefined)
                    this.activeKey = keyResult;
                else
                    return { value: true, fireEvent: true };
                if (!this.activeKey || !this.activeKey.accessKeys || this.activeKey.accessKeys.length == 0) {
                    if (this.activeKey && this.activeKey.manualStopProcessing) {
                        this.manualStopProcessing = true;
                        break;
                    }
                    return { value: true, fireEvent: true };
                }
        }
        return { value: false };
    },
    TryMoveFocusLeft: function (modifier) {},
    TryMoveFocusRight: function (modifier) {},
    TryMoveFocusUp: function (modifier) {},
    TryMoveFocusDown: function (modifier) {},
    FocusByAccessKey: function() {
        if (this.onFocusByAccessKey)
            this.onFocusByAccessKey();
        this.HideAccessKeys();
        KbdHelper.prototype.FocusByAccessKey.call(this);
        this.activeKey = this.accessKey;
        this.activeKey.execute();
        this.isActive = true;
        this.areAccessKeysShown = true;
    },
    HideAccessKeys: function() {
        this.areAccessKeysShown = false;
        this.hideAccessKeys(this.accessKey);
    },
    Update: function() {
        // 10 times in one second
        this.throttleMethod(this.refresh, 100);
    },
    refresh: function() {
        if(this.activeKey && this.areAccessKeysShown) {
            this.activeKey.execute();
        }
    },
    throttleMethod: function(method, delay) {
        clearTimeout(method.timerId);
        method.timerId = setTimeout(function() {
            method.call(this);
        }.aspxBind(this), delay);

    },
    AreAccessKeysShown: function() {
        return this.areAccessKeysShown;
    },

    hideAccessKeys: function (accessKey) {
        for (var i = 0, ak; ak = accessKey.accessKeys[i]; i++) {
            this.hideAccessKeys(ak);
        }
        if (accessKey)
            accessKey.hide();
    },
    HandleClick: function(e) {
        KbdHelper.prototype.HandleClick.call(this, e);
        this.stopProcessing();
    }
});

AccessKey = ASPx.CreateClass(null, {
    constructor: function (popupItem, getPopupElement, keyTipElement, key, onlyClick, manualStopProcessing) {
        this.key = key ? key : keyTipElement ? ASPxClientUtils.Trim(ASPx.GetInnerText(keyTipElement)) : null;
        this.popupItem = popupItem;
        this.getPopupElement = getPopupElement;
        this.keyTipElement = keyTipElement;
        this.accessKeys = [];
        this.needShowChilds = true;
        this.parent = null;
        this.onlyClick = onlyClick;
        this.manualStopProcessing = manualStopProcessing;
    },

    Add: function (accessKey) {
        this.accessKeys.push(accessKey);
        accessKey.parent = this;
    },

    TryAccessKey: function (char, index, needToContinue) {
        if (!this.accessKeys || this.accessKeys.length == 0)
            return;
        for (var i = 0, accessKey; accessKey = this.accessKeys[i]; i++) {
            if (accessKey.key[index] == char && accessKey.isVisible()) {
                if (accessKey.key[index + 1]) {
                    needToContinue.value = true;
                }
                else {
                    accessKey.execute();
                    return accessKey;
                }
            } else {
                accessKey.hide();
            }
        }
        for (var i = 0, accessKey; accessKey = this.accessKeys[i]; i++) {
            var key = accessKey.TryAccessKey(char, index, needToContinue);
            if (key)
                return key;
        }
        return;
    },

    isVisible: function(){
        return ASPx.GetElementVisibility(this.keyTipElement);
    },

    Return: function () {
        this.hideChildAccessKeys();
        if (this.parent) {
            this.parent.showAccessKeys(true);
        }        
        return this.parent;
    },

    execute: function () {
        this.hideAll();
        if (this.popupItem && this.popupItem.accessKeyClick && !this.onlyClick)
            this.popupItem.accessKeyClick();
        if (this.getPopupElement && this.onlyClick)
            ASPx.Evt.EmulateMouseClick(this.getPopupElement(this.popupItem));
        if (this.accessKeys)
            setTimeout(function () {
                this.showAccessKeys(true);
            }.aspxBind(this), 100);
    },

    showAccessKeys: function(directShow) {
        if (!directShow && !this.needShowChilds)
            return;
        for (var i = 0; i < this.accessKeys.length; i++) {
            var accessKey = this.accessKeys[i];
            if (accessKey) {
                var popupElement = accessKey.getPopupElement ? accessKey.getPopupElement(accessKey.popupItem) : null;
                if (popupElement && this.isElementVisible(popupElement)) {
                    this.show(accessKey);
                }
                accessKey.showAccessKeys();
            }
        }
    },
    isElementVisible: function (el) { return ASPx.IsElementVisible(el, true); },
    show: function(accessKey) {
        var keyTipElement = accessKey.keyTipElement;
        var popupElement = accessKey.getPopupElement(accessKey.popupItem);
        this.showKeyTipElement(keyTipElement, this.calculateCoordinates(accessKey, keyTipElement, popupElement));
    },
    showKeyTipElement: function (keyTipElement, coordinates) {
        ASPx.SetAbsoluteY(keyTipElement, coordinates.top);
        ASPx.SetAbsoluteX(keyTipElement, coordinates.left);
        ASPx.SetElementVisibility(keyTipElement, true); 
    },
    calculateCoordinates: function (accessKey, keyTipElement, popupElement) {
        var top = ASPx.GetAbsolutePositionY(popupElement);
        var left = ASPx.GetAbsolutePositionX(popupElement);
        
        if (accessKey.popupItem.getAccessKeyPosition)
            switch (accessKey.popupItem.getAccessKeyPosition()) {
                case "AboveRight":
                    left = left + popupElement.offsetWidth - keyTipElement.offsetWidth / 3;
                    top = top - keyTipElement.offsetHeight / 2;
                    break;
                case "Right":
                    left = left + popupElement.offsetWidth - keyTipElement.offsetWidth / 3;
                    top = top + popupElement.offsetHeight / 2 - keyTipElement.offsetHeight / 2;
                    break;
                case "BelowRight":
                    left = left + popupElement.offsetWidth - keyTipElement.offsetWidth / 3;
                    top = top + keyTipElement.offsetHeight / 2;
                    break;
                default:
                    top = top + popupElement.offsetHeight;
                    left = left + popupElement.offsetWidth / 2 - keyTipElement.offsetWidth / 2;
                    break;
            }
        else {
            top = top + popupElement.offsetHeight;
            left = left + popupElement.offsetWidth / 2 - keyTipElement.offsetWidth / 2;
        }
        return { top: top, left: left };
    },

    hide: function() {
        if (this.keyTipElement)
            ASPx.SetElementVisibility(this.keyTipElement, false);
    },

    hideChildAccessKeys: function () {
        this.hideAccessKeys(this.accessKeys);
    },

    hideAccessKeys: function (accessKeys) {
        if (accessKeys) {
            for (var i = 0, accessKey; accessKey = accessKeys[i]; i++) {
                if (accessKey.keyTipElement)
                    accessKey.hide();
                accessKey.hideChildAccessKeys();
            }
        }
    },
    hideAll: function () {
        this.getRoot(this).hideChildAccessKeys();
    },
    getRoot: function (accessKey) {
        if (!accessKey.parent)
            return accessKey;
        return this.getRoot(accessKey.parent);
    }
});

