            cellObject.C = [cellRect.y, cellRect.x];
            cellObject.BC = null;
            cellObject.SW = null;
            cellObject.CE = {};
            cellObject.CE.S = [cellRect.width - 4, cellRect.height];
            this.setCellValueToObject(cellObject, cellValue);
            this.setCellStyleToObject(cellObject, cellStyle);

            return cellObject;
