            this.initPosX = getAbsoluteX(this.resizingPanel);
            this.initPosY = getAbsoluteY(this.resizingPanel);

            this.initWidth = this.resizingPanel.offsetWidth;
            this.initHeight = this.resizingPanel.offsetHeight;

